import React from 'react';

const Hero = () => {
  return (
    <section id="hero" className="bg-gray-200 p-8 text-center">
      <h2 className="text-3xl font-bold mb-4">Welcome to Our Service</h2>
      <p className="mb-4">Discover the amazing features we offer.</p>
      <button className="bg-blue-500 text-white px-4 py-2 rounded">Get Started</button>
    </section>
  );
};

export default Hero;
